import React from "react";
import CanvasJSReact from "@canvasjs/react-charts";

var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const BarChart = ({ dataPoints }) => {
  // Determine the index of the bar to highlight
  const highlightIndex = dataPoints.findIndex((point) => point.y > 20); // Example condition

  // Split dataPoints into two arrays
  const normalDataPoints = dataPoints.filter((_, i) => i !== highlightIndex);
  const highlightedDataPoint = dataPoints.filter(
    (_, i) => i === highlightIndex
  );

  const options = {
    backgroundColor: "#1e293b",
    axisX: {
      interval: 1,
      labelFontColor: "white",
      tickColor: "white",
    },
    axisY: {
      labelFontColor: "white",
      tickColor: "white",
    },
    data: [
      {
        type: "column",
        dataPoints: normalDataPoints,
        color: "#FF5733", // Normal bars color
      },
      {
        type: "column",
        dataPoints: highlightedDataPoint,
        color: "#00FF00", // Highlighted bar color
      },
    ],
  };

  return (
    <div>
      <CanvasJSChart options={options} />
    </div>
  );
};

export default BarChart;
