import React from "react";
import useOnclickOutside from "react-cool-onclickoutside";
import CanvasJSReact from "@canvasjs/react-charts";

export const ResultStatistics = ({ gameStats, setShowResults, resetGame }) => {
  const ref = useOnclickOutside(() => {
    setShowResults(false);
    resetGame();
  });

  var CanvasJS = CanvasJSReact.CanvasJS;
  var CanvasJSChart = CanvasJSReact.CanvasJSChart;

  class App extends Component {
    render() {
      const options = {
        title: {
          text: "Basic Column Chart in React",
        },
        data: [
          {
            type: "column",
            dataPoints: [
              { label: "Apple", y: 10 },
              { label: "Orange", y: 15 },
              { label: "Banana", y: 25 },
              { label: "Mango", y: 30 },
              { label: "Grape", y: 28 },
            ],
          },
        ],
      };
      return (
        <div>
          <CanvasJSChart
            options={options}
            /* onRef = {ref => this.chart = ref} */
          />
          {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
        </div>
      );
    }
  }

  return (
    <>
      <div className="resultStats bg-opacity-75">
        {gameStats.guessResult}
        <div
          ref={ref}
          className="w-2/5 h-5/6 flex justify-left p-10 rounded-lg bg-gray-800 z-10 absolute"
        >
          <div>
            <h1>You guessed Correct! </h1>

            <p>Score: {gameStats.score}</p>
            <CanvasJSChart options={options} />
          </div>
        </div>
      </div>
    </>
  );
};
export default ResultStatistics;
