import React, { useState } from "react";
import {
  AiFillGithub,
  AiFillSetting,
  AiFillQuestionCircle,
} from "react-icons/ai";
import SettingsMenu from "./SettingsMenu";

const NavBar = () => {
  return (
    <div
      className="top-0 left-0 w-full h-14 m-0 
    flex flex-row justify-between items-center bg-gray-700 text-white shadow-md"
    >
      <h1 className="ml-4 text-yellow-500">Guessipedia</h1>
      <NavBarIcons />
    </div>
  );
};

const NavBarIcons = () => {
  const [showSettings, setShowSettings] = useState(false);

  return (
    <div>
      <h1>Settings</h1>
      <div
        className="top-0 left-0 w-full h-10 m-0 
      flex flex-row justify-end items-center bg-gray-700 text-white "
      >
        <Icon
          icon={
            <AiFillSetting size="24" onClick={() => setShowSettings(true)} />
          }
        />
        <Icon icon={<AiFillQuestionCircle size="24" />} />
        <Icon
          icon={<AiFillGithub size="24" />}
          onClick={() => window.open("https://github.com", "_blank")}
        />
      </div>
    </div>
  );
};

const Icon = ({ icon, onClick }) => {
  return (
    <a onClick={onClick} className="sidebar-icon mx-2">
      {icon}
    </a>
  );
};

export default NavBar;
