import logo from "./logo.svg";
import "./App.css";
import NavBar from "./components/NavBar";
import { Route, Routes } from "react-router-dom";
import SettingsMenu from "./components/pages/SettingsMenu";
import Home from "./components/pages/Home";
import { useState, useRef } from "react";
import { useTransition } from "react-spring";
import { Transition } from "react-transition-group"; // ES6

const duration = 300;

const defaultStyle = {
  transition: `opacity ${duration}ms ease-in-out`,
  opacity: 0,
};

const transitionStyles = {
  entering: { opacity: 1 },
  entered: { opacity: 1 },
  exiting: { opacity: 0 },
  exited: { opacity: 0 },
};
function App() {
  const [showSettings, setShowSettings] = useState(false);
  const nodeRef = useRef(null);
  return (
    <>
      {showSettings ? <SettingsMenu setShowSettings={setShowSettings} /> : null}

      <NavBar setShowSettings={setShowSettings} />
    </>
  );
}

export default App;
