    setShowResults(false);
    resetGame();
  });

  return (
    <>
      <div className="resultStats opacity-95">
        {gameStats.guessResult}
        <div
          ref={ref}
          className="w-4/5 h-4/5 flex items-center justify-center rounded-lg bg-gray-800 z-50 absolute opacity-100"
        >
          <p>butthole</p>
        </div>
      </div>
    </>
  );
};
export default ResultStatistics;
